import { Button, Card, CardContent, CircularProgress, Grid, TextField, Typography } from '@mui/material'
import { useState, useEffect } from 'react'
import { useNavigate, useParams } from 'react-router-dom'

export default function TaskForm() {


  const [task, setTask] = useState({
    title: '',
    description: '',
  })

  const [loading, setLoading] = useState(false)
  const [editing, setEditing] = useState(false)

  const navigate = useNavigate()
  const params = useParams()

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true)

    if (editing) {
      await fetch(`http://localhost:4000/tasks/${params.id}`), {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json'},
        body: JSON.stringify(task),
      }
    } else {
      await fetch('http://localhost:4000/tasks', {
        method: 'POST',
        body: JSON.stringify(task),
        headers: { 'Content-Type': 'application/json' },
      })
    }



    setLoading(false)

    navigate('/')
  };

  const handleChange = e =>
    setTask({ ...task, [e.target.name]: e.target.value });


  const loadTask = async (id) => {
    const res = await fetch(`http://localhost:4000/tasks/${params.id}`)
    const data = await res.json()
    setTask({ title: data.title, description: data.description })
    setEditing(true)
  }


  useEffect(() => {
    if (params.id) {
      loadTask(params.id);
    }
  })

  return (
    <Grid container direction='column' alignItems='center' justifyContent='center'>
      <Grid item xs={3}>
        <Card sx={{ mt: 5 }} style={{ backgroundColor: '#1e272e', padding: '1rem' }}>
          <Typography variant='5' textAlign='center' color='white'>
            Create Task
          </Typography>
          <CardContent>
            <form onSubmit={handleSubmit}>
              <TextField
                name='title'
                value={task.title}
                variant='filled'
                label='Write your title'
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' } }} />
              <TextField
                name='description'
                value={task.description}
                ariant='filled'
                label='Write your description'
                multiline
                rows={4} sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' } }} />
              <Button variant='contained' color='primary' type='submit' disabled={!task.title || !task.description}>
                {loading ? <CircularProgress
                  color='inherit'
                  size={24}
                /> : 'Create'}
              </Button>
            </form>
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  )
}
