import { Button, Card, CardContent, CircularProgress, Grid, TextField, Typography } from '@mui/material';
import { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

export default function EditClient() {
  const [task, setTask] = useState({
    rut: '',
    nombre: '',
    direccion: '',
  });

  const [loading, setLoading] = useState(false);
  const [editing, setEditing] = useState(false);

  const navigate = useNavigate();
  const params = useParams();

  const handleChange = (e) =>
    setTask({ ...task, [e.target.name]: e.target.value });

  const loadClientData = async (id) => {
    try {
      const response = await fetch(`http://localhost:4000/clientes/${id}`);
      if (response.status === 200) {
        const data = await response.json();
        setTask({ rut: data.rut, nombre: data.nombre, direccion: data.direccion });
        setEditing(true);
      } else {
        console.error('Cliente no encontrado');
      }
    } catch (error) {
      console.error('Error al cargar el cliente:', error);
    }
  };

  useEffect(() => {
    if (params.id) {
      loadClientData(params.id);
    }
  }, [params.id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const apiUrl = editing
        ? `http://localhost:4000/clientes/${params.id}`
        : 'http://localhost:4000/clientes';

      const method = editing ? 'PUT' : 'POST';

      const response = await fetch(apiUrl, {
        method,
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(task),
      });

      if (response.status === 200 || response.status === 201) {
        navigate('/');
      } else {
        console.error('Error al guardar el cliente');
      }
    } catch (error) {
      console.error('Error en la solicitud:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Grid container direction="column" alignItems="center" justifyContent="center">
      <Grid item xs={3}>
        <Card sx={{ mt: 5 }} style={{ backgroundColor: '#1e272e', padding: '1rem' }}>
          <Typography variant="h5" textAlign="center" color="white">
            {editing ? "Editar Cliente" : "Agregar Cliente"}
          </Typography>
          <CardContent>
            <form onSubmit={handleSubmit}>
              <TextField
                name="rut"
                value={task.rut}
                variant="filled"
                label="RUT o Código"
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' }}}
              />
              <TextField
                name="nombre"
                value={task.nombre}
                variant="filled"
                label="Nombre"
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' }}}
                InputLabelProps={{ style: { color: 'white' }}}
              />
              <TextField
                name="direccion"
                value={task.direccion}
                variant="filled"
                label="Dirección"
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' }}}
                InputLabelProps={{ style: { color: 'white' }}}
              />
              <Button variant="contained" color="primary" type="submit" disabled={!task.rut || !task.nombre}>
                {loading ? <CircularProgress color="inherit" size={24} /> : 'Guardar'}
              </Button>
            </form>
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );
}
