import { Button, Card, CardContent, CircularProgress, Grid, TextField, Typography } from '@mui/material';
import { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

export default function NewMedidor() {
    const [task, setTask] = useState({
        rut: '',
        codigo: '',
        nombre: '',
        fecha_creacion: '',
        descripcion: '',
    });

    const [medidor, setMedidor] = useState({
        codigo: '',
        nombre: '',
        fecha_creacion: '',
        descripcion: '',
    });

    const [loading, setLoading] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');

    const navigate = useNavigate();
    const params = useParams();

    useEffect(() => {
        // Realiza una solicitud al servidor para obtener la lista de medidores del cliente y contarlos
        async function fetchMedidoresCount() {
            try {
                const res = await fetch(`http://localhost:4000/medidores/cliente/${params.id}`);
                const data = await res.json();
                const medidoresCount = data.length;
                if (medidoresCount >= 3) {
                    setErrorMessage('El cliente ya tiene 3 medidores registrados.');
                } else {
                    setErrorMessage('');
                }
            } catch (error) {
                console.error(error);
            }
        }
        if (params.id) {
            fetchMedidoresCount();
        }
    }, [params.id]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        // Validación adicional antes de enviar
        if (!validateRut(task.rut) || !validateCodigo(task.codigo)) {
            setLoading(false);
            return;
        }

        const aux = { rut: task.rut, ...medidor };

        // Realiza la solicitud POST solo si no hay un error
        if (!errorMessage) {
            try {
                await fetch('http://localhost:4000/medidores', {
                    method: 'POST',
                    body: JSON.stringify(aux),
                    headers: { 'Content-Type': 'application/json' },
                });
                setLoading(false);
                navigate('/');
            } catch (error) {
                console.error(error);
            }
        } else {
            setLoading(false);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;

        if (name === 'nombre' && !/^[a-zA-Z\s]*$/.test(value)) {
            return;
        }

        setMedidor({ ...medidor, [name]: value });
    }

    const validateRut = (rut) => {
        const rutRegex = /^\d{1,2}.\d{3}.\d{3}[-][0-9kK]{1}$/;
        return rutRegex.test(rut);
    };

    const validateCodigo = (codigo) => {
        const codigoRegex = /^[a-zA-Z0-9]*$/;
        return codigoRegex.test(codigo);
    };

    return (
        <Grid container direction='column' alignItems='center' justifyContent='center'>
            <Grid item xs={3}>
                <Card sx={{ mt: 5 }} style={{ backgroundColor: '#1e272e', padding: '1rem' }}>
                    <Typography variant='5' textAlign='center' color='white'>
                        Agregar
                    </Typography>
                    <CardContent>
                        <form onSubmit={handleSubmit}>
                            <TextField
                                name='rut'
                                value={task.rut}
                                variant='filled'
                                label='Rut'
                                sx={{ display: 'block', margin: '.5rem 0' }}
                                onChange={handleChange}
                                inputProps={{ style: { color: 'white' } }}
                                InputLabelProps={{ style: { color: 'white' } }}
                                error={!validateRut(task.rut)}
                                helperText={!validateRut(task.rut) ? 'RUT no válido' : ''}
                            />
                            <TextField
                                name='codigo'
                                value={medidor.codigo}
                                variant='filled'
                                label='Código'
                                sx={{ display: 'block', margin: '.5rem 0' }}
                                onChange={handleChange}
                                inputProps={{ style: { color: 'white' } }}
                                InputLabelProps={{ style: { color: 'white' } }}
                                error={!validateCodigo(medidor.codigo)}
                                helperText={!validateCodigo(medidor.codigo) ? 'Código no válido' : ''}
                            />
                            <TextField
                                name='nombre'
                                value={medidor.nombre}
                                variant='filled'
                                label='Nombre'
                                sx={{ display: 'block', margin: '.5rem 0' }}
                                onChange={handleChange}
                                inputProps={{ style: { color: 'white' } }}
                                InputLabelProps={{ style: { color: 'white' } }}
                            />
                            <TextField
                                name='fecha_creacion'
                                value={medidor.fecha_creacion}
                                type='date'
                                variant='filled'
                                label='Fecha de Creación'
                                sx={{ display: 'block', margin: '.5rem 0' }}
                                onChange={handleChange}
                                inputProps={{ style: { color: 'white' } }}
                                InputLabelProps={{ style: { color: 'white' } }}
                            />
                            <TextField
                                name='descripcion'
                                value={medidor.descripcion}
                                variant='filled'
                                label='Descripción'
                                multiline
                                rows={4}
                                sx={{ display: 'block', margin: '.5rem 0' }}
                                onChange={handleChange}
                                inputProps={{ style: { color: 'white' } }}
                                InputLabelProps={{ style: { color: 'white' } }}
                            />
                            {errorMessage && (
                                <Typography variant="body2" color="error">
                                    {errorMessage}
                                </Typography>
                            )}
                            <Button variant='contained' color='primary' type='submit' disabled={!validateCodigo(medidor.codigo) || !validateRut(task.rut) || !medidor.nombre || !medidor.fecha_creacion || errorMessage}>
                                {loading ? <CircularProgress
                                    color='inherit'
                                    size={24}
                                /> : 'Guardar'}
                            </Button>
                        </form>
                    </CardContent>
                </Card>
            </Grid>
        </Grid>
    );
}
