import { Button, Card, CardContent, CircularProgress, Grid, TextField, Typography } from '@mui/material';
import { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

export default function TaskForm() {
  const [task, setTask] = useState({
    rut: '', // Cambiar a 'rut' en lugar de 'title'
    nombre: '', // Cambiar a 'nombre' en lugar de 'description'
    direccion: '', // Agregar 'direccion' para clientes
    codigo: '', // Agregar 'codigo' para medidores
    fecha_creacion: '', // Agregar 'fecha_creacion' para medidores
    descripcion: '', // Agregar 'descripcion' 
  });

  const [loading, setLoading] = useState(false);
  const [editing, setEditing] = useState(false);

  const navigate = useNavigate();
  const params = useParams();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
  
    const isCliente = params.id.includes("clientes");
    const endpoint = isCliente ? "clientes" : "medidores";
    const apiUrl = isCliente
      ? `http://localhost:4000/clientes`
      : `http://localhost:4000/medidores`;
  
    try {
      const response = await fetch(
        editing
          ? `${apiUrl}/${params.id}`
          : apiUrl,
        {
          method: editing ? "PUT" : "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(task),
        }
      );
  
      if (response.ok) {
        setLoading(false);
        navigate('/');
      } else {
        console.error("Error in API request");
        // Handle the error (e.g., show an error message to the user)
      }
    } catch (error) {
      console.error("Error in API request:", error);
      // Handle the error (e.g., show an error message to the user)
    }
  };
  
  const handleChange = (e) => {
    const { name, value } = e.target;
    setTask((prevTask) => ({
      ...prevTask,
      [name]: value,
    }));
  };
  
  const loadTask = async (id) => {
    const isCliente = id.includes("clientes");
    const endpoint = isCliente ? "clientes" : "medidores";
  
    try {
      const res = await fetch(`http://localhost:4000/${endpoint}/${id}`);
      if (res.status === 404) {
        // Handle not found case
        console.log(`No ${isCliente ? "Cliente" : "Medidor"} found with ID ${id}`);
        return;
      }
      const data = await res.json();
      setTask({
        rut: data.rut,
        nombre: data.nombre,
        direccion: isCliente ? data.direccion : "",
        codigo: isCliente ? "" : data.codigo,
        fecha_creacion: isCliente ? "" : data.fecha_creacion,
        descripcion: isCliente ? "" : data.descripcion,
      });
      setEditing(true);
    } catch (error) {
      console.error("Error loading data:", error);
      // Handle error (e.g., show an error message to the user)
    }
  };
  
  useEffect(() => {
    if (params.id) {
      loadTask(params.id);
    }
  }, [params.id]);
  

  return (
    <Grid container direction='column' alignItems='center' justifyContent='center'>
      <Grid item xs={3}>
        <Card sx={{ mt: 5 }} style={{ backgroundColor: '#1e272e', padding: '1rem' }}>
          <Typography variant='h5' textAlign='center' color='white'>
            {editing ? "Editar Cliente/Medidor" : "Agregar Cliente/Medidor"}
          </Typography>
          <CardContent>
            <form onSubmit={handleSubmit}>
              <TextField
                name='rut'
                value={task.rut}
                variant='filled'
                label='RUT o C贸digo'
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' } }}
              />
              <TextField
                name='nombre'
                value={task.nombre}
                variant='filled'
                label='Nombre'
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' } }}
              />

              <TextField
                name='direccion'
                value={task.direccion}
                variant='filled'
                label='Direcci贸n'
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' } }}
              />

              <TextField
                name='codigo'
                value={task.codigo}
                variant='filled'
                label='C贸digo'
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' } }}
              />

              <TextField
                name='fecha_creacion'
                value={task.fecha_creacion}
                variant='filled'
                label='Fecha de Creaci贸n'
                type='date'
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' } }}
              />

              <TextField
                name='descripcion'
                value={task.descripcion}
                variant='filled'
                label='Descripcion'
                multiline
                rows={4} sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' } }}
              />



              <Button variant='contained' color='primary' type='submit' disabled={!task.rut || !task.nombre}>
                {loading ? <CircularProgress color='inherit' size={24} /> : 'Guardar'}
              </Button>
            </form>
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );
}
