import { Button, Card, CardContent, CircularProgress, Grid, TextField, Typography } from '@mui/material';
import { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

export default function TaskForm() {
  const [task, setTask] = useState({
    rut: '',
    nombre: '',
    direccion: '',
    codigo: '',
    fecha_creacion: '',
    descripcion: '',
  });

  const [loading, setLoading] = useState(false);
  const [editing, setEditing] = useState(false);

  const navigate = useNavigate();
  const params = useParams();

  const isClientForm = window.location.href.includes("clientes"); // Comprueba si es un formulario de clientes o medidores

  const apiUrl = isClientForm
    ? `http://localhost:4000/clientes/${params.id}`
    : `http://localhost:4000/medidores/${params.id}`;

  const handleData = async () => {
    try {
      const response = await fetch(apiUrl);

      if (response.ok) {
        const data = await response.json();
        setTask(data);
        setEditing(true);
      } else {
        // Handle errors here
      }
    } catch (error) {
      // Handle fetch error here
    }
  };

  useEffect(() => {
    if (params.id) {
      handleData();
    }
  }, [params.id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    const method = editing ? 'PUT' : 'POST';
    const endpoint = isClientForm ? 'clientes' : 'medidores';

    try {
      const response = await fetch(`http://localhost:4000/${endpoint}/${params.id}`, {
        method,
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(task),
      });

      if (response.ok) {
        setLoading(false);
        navigate('/');
      } else {
        // Handle errors here
      }
    } catch (error) {
      // Handle fetch error here
    }
  };

  const handleChange = (e) =>
    setTask({ ...task, [e.target.name]: e.target.value });

  return (
    <Grid container direction='column' alignItems='center' justifyContent='center'>
      <Grid item xs={3}>
        <Card sx={{ mt: 5 }} style={{ backgroundColor: '#1e272e', padding: '1rem' }}>
          <Typography variant='h5' textAlign='center' color='white'>
            {editing ? `Editar ${isClientForm ? 'Cliente' : 'Medidor'}` : `Agregar ${isClientForm ? 'Cliente' : 'Medidor'}`}
          </Typography>
          <CardContent>
            <form onSubmit={handleSubmit}>
              <TextField
                name='rut'
                value={task.rut}
                variant='filled'
                label='RUT o Código'
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' } }}
              />
              <TextField
                name='nombre'
                value={task.nombre}
                variant='filled'
                label='Nombre'
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' } }}
              />
              <TextField
                name='direccion'
                value={task.direccion}
                variant='filled'
                label='Dirección'
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' } }}
              />
              <TextField
                name='codigo'
                value={task.codigo}
                variant='filled'
                label='Código'
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' } }}
              />
              <TextField
                name='fecha_creacion'
                value={task.fecha_creacion}
                variant='filled'
                label='Fecha de Creación'
                type='date'
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' } }}
              />
              <TextField
                name='descripcion'
                value={task.descripcion}
                variant='filled'
                label='Descripcion'
                multiline
                rows={4} sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' } }}
              />

              <Button variant='contained' color='primary' type='submit' disabled={!task.rut || !task.nombre}>
                {loading ? <CircularProgress color='inherit' size={24} /> : 'Guardar'}
              </Button>
            </form>
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );
}
