import { useEffect, useState } from 'react';
import { Button, Card, CardContent, Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';

export default function TaskList() {
    const [tasks, setTasks] = useState([]);
    const navigate = useNavigate();
    const isClientList = window.location.href.includes("clientes");

    const loadTasks = async () => {
        const endpoint = isClientList ? "clientes" : "medidores";
        const response = await fetch(`http://localhost:4000/${endpoint}`);
        const data = await response.json();
        setTasks(data);
    };

    const handleDelete = async (id) => {
        try {
            const endpoint = isClientList ? "clientes" : "medidores";
            await fetch(`http://localhost:4000/${endpoint}/${id}`, {
                method: "DELETE",
            });

            setTasks(tasks.filter(task => task.id !== id));
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        loadTasks();
    }, [isClientList]);

    return (
        <>
            <h1>{isClientList ? "Lista de Clientes" : "Lista de Medidores"}</h1>
            {tasks.map((task) => (
                <Card
                    style={{
                        marginBottom: '.7rem',
                        backgroundColor: '#1e272e',
                    }}
                    key={task.id}
                >
                    <CardContent
                        style={{
                            display: 'flex',
                            justifyContent: 'space-between',
                        }}
                    >
                        <div style={{ color: 'white' }}>
                            <Typography>{isClientList ? "RUT" : "Código"}: {isClientList ? task.rut : task.codigo}</Typography>
                            <Typography>Nombre: {task.nombre}</Typography>
                            {/* Agrega campos adicionales específicos para clientes o medidores si es necesario */}
                        </div>
                        <div>
                            <Button
                                variant='contained'
                                color='inherit'
                                onClick={() => navigate(`cliente/${task.id}/edit`)}
                            >
                                EDIT
                            </Button>
                            <Button
                                variant='contained'
                                color='warning'
                                onClick={() => handleDelete(task.id)}
                                style={{ marginLeft: '.5rem' }}
                            >
                                DELETE
                            </Button>
                        </div>
                    </CardContent>
                </Card>
            ))}
        </>
    );
}
