import { Button, Card, CardContent, CircularProgress, Grid, TextField, Typography } from '@mui/material';
import { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

export default function TaskForm() {
  const [task, setTask] = useState({
    rut: '',
    nombre: '',
    direccion: '',
    codigo: '',
    fecha_creacion: '',
  });

  const [loading, setLoading] = useState(false);
  const [editing, setEditing] = useState(false);

  const navigate = useNavigate();
  const params = useParams();

  // Funci贸n para guardar un cliente
  const saveCliente = async () => {
    await fetch('http://localhost:4000/clientes', {
      method: 'POST',
      body: JSON.stringify(task),
      headers: { 'Content-Type': 'application/json' },
    });
  };

  // Funci贸n para guardar un medidor
  const saveMedidor = async () => {
    await fetch('http://localhost:4000/medidores', {
      method: 'POST',
      body: JSON.stringify(task),
      headers: { 'Content-Type': 'application/json' },
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    if (editing) {
      await fetch(`http://localhost:4000/clientes/${params.id}`, {
        method: 'PUT',
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(task),
      });
    } else {
      if (params.type === 'clientes') {
        saveCliente(); // Llama a la funci贸n de guardar cliente
      } else if (params.type === 'medidores') {
        saveMedidor(); // Llama a la funci贸n de guardar medidor
      }
    }

    setLoading(false);
    navigate('/');
  };

  const handleChange = (e) =>
    setTask({ ...task, [e.target.name]: e.target.value });

  const loadTask = async (id) => {
    const res = await fetch(`http://localhost:4000/${params.type}/${params.id}`);
    const data = await res.json();
    setTask({ ...data });
    setEditing(true);
  };

  useEffect(() => {
    if (params.id) {
      loadTask(params.id);
    }
  }, [params.id, params.type]);

  return (
    <Grid container direction='column' alignItems='center' justifyContent='center'>
      <Grid item xs={3}>
        <Card sx={{ mt: 5 }} style={{ backgroundColor: '#1e272e', padding: '1rem' }}>
          <Typography variant='h5' textAlign='center' color='white'>
            {editing ? "Editar Cliente/Medidor" : "Agregar Cliente/Medidor"}
          </Typography>
          <CardContent>
            <form onSubmit={handleSubmit}>
              <TextField
                name='rut'
                value={task.rut}
                variant='filled'
                label='RUT o C贸digo'
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' } }}
              />
              <TextField
                name='nombre'
                value={task.nombre}
                variant='filled'
                label='Nombre'
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' } }}
              />

              {params.type === 'clientes' && (
                <TextField
                  name='direccion'
                  value={task.direccion}
                  variant='filled'
                  label='Direcci贸n'
                  sx={{ display: 'block', margin: '.5rem 0' }}
                  onChange={handleChange}
                  inputProps={{ style: { color: 'white' } }}
                  InputLabelProps={{ style: { color: 'white' } }}
                />
              )}

              {params.type === 'medidores' && (
                <div>
                  <TextField
                    name='codigo'
                    value={task.codigo}
                    variant='filled'
                    label='C贸digo'
                    sx={{ display: 'block', margin: '.5rem 0' }}
                    onChange={handleChange}
                    inputProps={{ style: { color: 'white' } }}
                    InputLabelProps={{ style: { color: 'white' } }}
                  />
                  <TextField
                    name='fecha_creacion'
                    value={task.fecha_creacion}
                    variant='filled'
                    label='Fecha de Creaci贸n'
                    type='date'
                    sx={{ display: 'block', margin: '.5rem 0' }}
                    onChange={handleChange}
                    inputProps={{ style: { color: 'white' } }}
                    InputLabelProps={{ style: { color: 'white' } }}
                  />
                </div>
              )}

              <Button variant='contained' color='primary' type='submit' disabled={!task.rut || !task.nombre}>
                {loading ? <CircularProgress color='inherit' size={24} /> : 'Guardar'}
              </Button>
            </form>
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );
}
