import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

export default function NewMedidor() {
  const [medidor, setMedidor] = useState({
    rut: '',
    codigo: '',
    nombre: '',
    fecha_creacion: '',
    descripcion: '',
  });
  
  const [taskData, setTaskData] = useState({});
  const [medidoresData, setMedidoresData] = useState([]);

  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const params = useParams();

  useEffect(() => {
    // Fetch data from 'task/id'
    fetch(`http://localhost:4000/task/${params.id}`)
      .then((response) => response.json())
      .then((data) => setTaskData(data))
      .catch((error) => console.error('Error fetching task data:', error));

    // Fetch data from 'medidores'
    fetch('http://localhost:4000/medidores')
      .then((response) => response.json())
      .then((data) => setMedidoresData(data))
      .catch((error) => console.error('Error fetching medidores data:', error));
  }, [params.id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setMedidor((prevMedidor) => ({
      ...prevMedidor,
      [name]: value,
    }));
  };

  return (
    <Grid container direction="column" alignItems="center" justifyContent="center">
      <Grid item xs={3}>
        <Card sx={{ mt: 5 }} style={{ backgroundColor: '#1e272e', padding: '1rem' }}>
          <Typography variant="h5" textAlign="center" color="white">
            Agregar Medidor
          </Typography>
          <CardContent>
            <form onSubmit={handleSubmit}>
              <TextField
                name="rut"
                value={medidor.rut}
                variant="filled"
                label="Rut"
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' }}}
                InputLabelProps={{ style: { color: 'white' }}}
              />
              <TextField
                name="codigo"
                value={medidor.codigo}
                variant="filled"
                label="Código"
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' }}}
                InputLabelProps={{ style: { color: 'white' }}}
              />
              <TextField
                name="nombre"
                value={medidor.nombre}
                variant="filled"
                label="Nombre"
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' }}}
                InputLabelProps={{ style: { color: 'white' }}}
              />
              <TextField
                name="fecha_creacion"
                value={medidor.fecha_creacion}
                variant="filled"
                label="Fecha de Creación"
                type="date"
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' }}}
                InputLabelProps={{ style: { color: 'white' }}}
              />
              <TextField
                name="descripcion"
                value={medidor.descripcion}
                variant="filled"
                label="Descripcion"
                multiline
                rows={4} sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' }}}
                InputLabelProps={{ style: { color: 'white' }}}
              />
              <Button variant="contained" color="primary" type="submit" disabled={!medidor.rut || !medidor.codigo || !medidor.nombre || !medidor.fecha_creacion}>
                {loading ? <CircularProgress color="inherit" size={24} /> : 'Guardar'}
              </Button>
            </form>
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );
}
