import { Button, Card, CardContent, CircularProgress, Grid, TextField, Typography } from '@mui/material'
import { useState, useEffect } from 'react'
import { useNavigate, useParams } from 'react-router-dom'

export default function NewMedidor() {
    const [task, setTask] = useState({
        rut: '',
        codigo: '',
        nombre: '',
        fecha_creacion: '',
        descripcion: '',
    })

    const [loading, setLoading] = useState(false)

    const navigate = useNavigate()
    const params = useParams()

    const handleSubmit = async e => {
        e.preventDefault();
        setLoading(true);

        await fetch('http://localhost:4000/medidores', {
            method: 'POST',
            body: JSON.stringify(task),
            headers: { 'Content-Type': 'application/json' },
        });

        setLoading(false);
        navigate('/');
    };

    const handleChange = e => setTask({ ...task, [e.target.name]: e.target.value });

    const loadTask = async (id) => {
        const res = await fetch(`http://localhost:4000/tasks/rut/${id}`);
        const data = await res.json();
        setTask({ ...task, rut: data.rut });
    }

    useEffect(() => {
        if (params.id) {
            loadTask(params.id);
        }
    }, [params.id]);

    return (
        <Grid container direction='column' alignItems='center' justifyContent='center'>
            <Grid item xs={3}>
                <Card sx={{ mt: 5 }} style={{ backgroundColor: '#1e272e', padding: '1rem' }}>
                    <Typography variant='5' textAlign='center' color='white'>
                        Agregar
                    </Typography>
                    <CardContent>
                        <form onSubmit={handleSubmit}>
                            <TextField
                                name='rut'
                                value={task.rut}
                                variant='filled'
                                label='Rut'
                                sx={{ display: 'block', margin: '.5rem 0' }}
                                onChange={handleChange}
                                inputProps={{ style: { color: 'white' } }}
                                InputLabelProps={{ style: { color: 'white' } }}
                            />
                            {/* Resto de tus campos de entrada aqu√≠ */}
                            <Button variant='contained' color='primary' type='submit' disabled={!task.codigo || !task.nombre || !task.fecha_creacion}>
                                {loading ? <CircularProgress color='inherit' size={24} /> : 'Guardar'}
                            </Button>
                        </form>
                    </CardContent>
                </Card>
            </Grid>
        </Grid>
    )
}
