import { useEffect, useState } from 'react';
import { Button, Card, CardContent, Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';

export default function TaskList() {
    const [tasks, setTasks] = useState([]);
    const navigate = useNavigate();

    const loadTasks = async () => {
        const response = await fetch("http://localhost:4000/clientes"); // Cambia a la ruta adecuada para listar clientes
        const data = await response.json();
        setTasks(data);
    };

    const handleDelete = async (id) => {
        try {
            await fetch(`http://localhost:4000/clientes/${id}`, {
                method: "DELETE", // Cambia a la ruta adecuada para eliminar clientes
            });

            setTasks(tasks.filter(task => task.id !== id));
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        loadTasks();
    }, []);

    return (
        <>
            <h1>Task List</h1>
            {tasks.map((task) => (
                <Card
                    style={{
                        marginBottom: '.7rem',
                        backgroundColor: '#1e272e',
                    }}
                    key={task.id}
                >
                    <CardContent
                        style={{
                            display: 'flex',
                            justifyContent: 'space-between',
                        }}
                    >
                        <div style={{ color: 'white' }}>
                            <Typography>RUT: {task.rut || task.codigo}</Typography>
                            <Typography>Nombre: {task.nombre}</Typography>
                            {/* Agrega campos adicionales para clientes y medidores si es necesario */}
                        </div>
                        <div>
                            <Button
                                variant='contained'
                                color='inherit'
                                onClick={() => navigate(`tasks/${task.id}/edit`)}
                            >
                                EDIT
                            </Button>
                            <Button
                                variant='contained'
                                color='warning'
                                onClick={() => handleDelete(task.id)}
                                style={{ marginLeft: '.5rem' }}
                            >
                                DELETE
                            </Button>
                        </div>
                    </CardContent>
                </Card>
            ))}
        </>
    );
}
