import { Button, Card, CardContent, CircularProgress, Grid, TextField, Typography } from '@mui/material';
import { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

export default function TaskForm() {
  const [task, setTask] = useState({
    rut: '', // Cambiar a 'rut' en lugar de 'title'
    nombre: '', // Cambiar a 'nombre' en lugar de 'description'
    direccion: '', // Agregar 'direccion' para clientes
    codigo: '', // Agregar 'codigo' para medidores
    fecha_creacion: '', // Agregar 'fecha_creacion' para medidores
  });

  const [loading, setLoading] = useState(false);
  const [editing, setEditing] = useState(false);

  const navigate = useNavigate();
  const params = useParams();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    if (editing) {
      await fetch(`http://localhost:4000/clientes/${params.id}`, {
        method: 'PUT', // Cambiar a 'clientes' para clientes o 'medidores' para medidores
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(task),
      });
    } else {
      await fetch('http://localhost:4000/clientes', {
        method: 'POST', // Cambiar a 'clientes' para clientes o 'medidores' para medidores
        body: JSON.stringify(task),
        headers: { 'Content-Type': 'application/json' },
      });
    }

    setLoading(false);
    navigate('/');
  };

  const handleChange = (e) =>
    setTask({ ...task, [e.target.name]: e.target.value });

  const loadTask = async (id) => {
    const res = await fetch(`http://localhost:4000/clientes/${params.id}`); // Cambiar a 'clientes' para clientes o 'medidores' para medidores
    const data = await res.json();
    setTask({ rut: data.rut, nombre: data.nombre, direccion: data.direccion, codigo: data.codigo, fecha_creacion: data.fecha_creacion });
    setEditing(true);
  };

  useEffect(() => {
    if (params.id) {
      loadTask(params.id);
    }
  }, [params.id]);

  return (
    <Grid container direction='column' alignItems='center' justifyContent='center'>
      <Grid item xs={3}>
        <Card sx={{ mt: 5 }} style={{ backgroundColor: '#1e272e', padding: '1rem' }}>
          <Typography variant='h5' textAlign='center' color='white'>
            {editing ? "Editar Cliente/Medidor" : "Agregar Cliente/Medidor"}
          </Typography>
          <CardContent>
            <form onSubmit={handleSubmit}>
              <TextField
                name='rut'
                value={task.rut}
                variant='filled'
                label='RUT o Código'
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' } }}
              />
              <TextField
                name='nombre'
                value={task.nombre}
                variant='filled'
                label='Nombre'
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' } }}
              />
              {task.direccion && (
                <TextField
                  name='direccion'
                  value={task.direccion}
                  variant='filled'
                  label='Dirección'
                  sx={{ display: 'block', margin: '.5rem 0' }}
                  onChange={handleChange}
                  inputProps={{ style: { color: 'white' } }}
                  InputLabelProps={{ style: { color: 'white' }}}
                />
              )}
              {task.codigo && (
                <TextField
                  name='codigo'
                  value={task.codigo}
                  variant='filled'
                  label='Código'
                  sx={{ display: 'block', margin: '.5rem 0' }}
                  onChange={handleChange}
                  inputProps={{ style: { color: 'white' }}}
                  InputLabelProps={{ style: { color: 'white' }}}
                />
              )}
              {task.fecha_creacion && (
                <TextField
                  name='fecha_creacion'
                  value={task.fecha_creacion}
                  variant='filled'
                  label='Fecha de Creación'
                  type='date'
                  sx={{ display: 'block', margin: '.5rem 0' }}
                  onChange={handleChange}
                  inputProps={{ style: { color: 'white' }}}
                  InputLabelProps={{ style: { color: 'white' }}}
                />
              )}
              <Button variant='contained' color='primary' type='submit' disabled={!task.rut || !task.nombre}>
                {loading ? <CircularProgress color='inherit' size={24} /> : 'Guardar'}
              </Button>
            </form>
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );
}
