import { Button, Card, CardContent, CircularProgress, Grid, TextField, Typography } from '@mui/material';
import { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

export default function EditClient() {
    const [task, setTask] = useState({
        rut: '',
        nombre: '',
        direccion: '',
      })
    
      const [loading, setLoading] = useState(false)
      const [editing, setEditing] = useState(false)
    
      const navigate = useNavigate()
      const params = useParams()
    
      const handleSubmit = async e => {
        e.preventDefault();
        setLoading(true)
    
        if (editing) {
          await fetch(`http://localhost:4000/tasks/${params.id}`, {
            method: 'PUT',
            headers: {
              "Content-Type": "application/json"
            },
            body: JSON.stringify(task),
          })
        } else {
          await fetch('http://localhost:4000/tasks', {
            method: 'POST',
            body: JSON.stringify(task),
            headers: { 'Content-Type': 'application/json' },
          })
        }
    
        setLoading(false)
        navigate('/')
      };
    
      const handleChange = e =>
        setTask({ ...task, [e.target.name]: e.target.value });
    
    
      const loadTask = async (id) => {
        const res = await fetch(`http://localhost:4000/tasks/${params.id}`)
        const data = await res.json()
        setTask({ rut: data.rut, nombre: data.nombre, direccion: data.direccion})
        setEditing(true)
      }
    
    
      useEffect(() => {
        if (params.id) {
          loadTask(params.id);
        }
      }, [params.id]);

  return (
    <Grid container direction='column' alignItems='center' justifyContent='center'>
      <Grid item xs={3}>
        <Card sx={{ mt: 5 }} style={{ backgroundColor: '#1e272e', padding: '1rem' }}>
          <Typography variant='h5' textAlign='center' color='white'>
            {editing ? "Editar Cliente/Medidor" : "Agregar Cliente/Medidor"}
          </Typography>
          <CardContent>
            <form onSubmit={handleSubmit}>
              <TextField
                name='rut'
                value={task.rut}
                variant='filled'
                label='RUT o Código'
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' } }}
              />
              <TextField
                name='nombre'
                value={task.nombre}
                variant='filled'
                label='Nombre'
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' } }}
              />

              <TextField
                name='direccion'
                value={task.direccion}
                variant='filled'
                label='Dirección'
                sx={{ display: 'block', margin: '.5rem 0' }}
                onChange={handleChange}
                inputProps={{ style: { color: 'white' } }}
                InputLabelProps={{ style: { color: 'white' } }}
              />

              <Button variant='contained' color='primary' type='submit' disabled={!task.rut || !task.nombre}>
                {loading ? <CircularProgress color='inherit' size={24} /> : 'Guardar'}
              </Button>
            </form>
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );
}
