import { useEffect, useState } from 'react';
import { Button, Card, CardContent, Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';

export default function TaskList() {
    const [clientes, setClientes] = useState([]);
    const [medidores, setMedidores] = useState([]);
    const navigate = useNavigate();

    const loadClientes = async () => {
        const response = await fetch("http://localhost:4000/clientes");
        const data = await response.json();
        setClientes(data);
    };

    const loadMedidores = async () => {
        const response = await fetch("http://localhost:4000/medidores");
        const data = await response.json();
        setMedidores(data);
    };

    const handleDelete = async (id, isCliente) => {
        try {
            await fetch(`http://localhost:4000/${isCliente ? 'clientes' : 'medidores'}/${id}`, {
                method: "DELETE",
            });

            if (isCliente) {
                setClientes(clientes.filter(cliente => cliente.id !== id));
            } else {
                setMedidores(medidores.filter(medidor => medidor.id !== id));
            }
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        loadClientes();
        loadMedidores();
    }, []);

    return (
        <>
            <h1>Clientes</h1>
            {clientes.map((cliente) => (
                <Card
                    style={{
                        marginBottom: '.7rem',
                        backgroundColor: '#1e272e',
                    }}
                    key={cliente.id}
                >
                    <CardContent
                        style={{
                            display: 'flex',
                            justifyContent: 'space-between',
                        }}
                    >
                        <div style={{ color: 'white' }}>
                            <Typography>RUT: {cliente.rut}</Typography>
                            <Typography>Nombre: {cliente.nombre}</Typography>
                            <Typography>DirecciÃ³n: {cliente.direccion}</Typography>
                            {/* Add additional fields for clientes if necessary */}
                        </div>
                        <div>
                            <Button
                                variant='contained'
                                color='inherit'
                                onClick={() => navigate(`clientes/${cliente.id}/edit`)}
                            >
                                EDIT
                            </Button>
                            <Button
                                variant='contained'
                                color='warning'
                                onClick={() => handleDelete(cliente.id, true)}
                                style={{ marginLeft: '.5rem' }}
                            >
                                DELETE
                            </Button>
                        </div>
                    </CardContent>
                </Card>
            ))}

            <h1>Medidores</h1>
            {medidores.map((medidor) => (
                <Card
                    style={{
                        marginBottom: '.7rem',
                        backgroundColor: '#1e272e',
                    }}
                    key={medidor.id}
                >
                    <CardContent
                        style={{
                            display: 'flex',
                            justifyContent: 'space-between',
                        }}
                    >
                        <div style={{ color: 'white' }}>
                            <Typography>Codigo: {medidor.codigo}</Typography>
                            <Typography>Fecha de CreaciÃ³n: {medidor.fecha_creacion}</Typography>
                            {/* Add additional fields for medidores if necessary */}
                        </div>
                        <div>
                            <Button
                                variant='contained'
                                color='inherit'
                                onClick={() => navigate(`medidores/${medidor.id}/edit`)}
                            >
                                EDIT
                            </Button>
                            <Button
                                variant='contained'
                                color='warning'
                                onClick={() => handleDelete(medidor.id, false)}
                                style={{ marginLeft: '.5rem' }}
                            >
                                DELETE
                            </Button>
                        </div>
                    </CardContent>
                </Card>
            ))}
        </>
    );
}
