import { Button, Card, CardContent, CircularProgress, Grid, TextField, Typography } from '@mui/material';
import { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

export default function NewMedidor() {
    const [task, setTask] = useState({
        rut: '',
        codigo: '',
        nombre: '',
        fecha_creacion: '',
        descripcion: '',
        medidor_id: null, // Initialize medidor_id as null
    });

    const [loading, setLoading] = useState(false);
    const [editing, setEditing] = useState(false);

    const navigate = useNavigate();
    const params = useParams();

    const loadMedidorId = async () => {
        const res = await fetch(`http://localhost:4000/medidores/${params.id}`);
        const data = await res.json();
        setTask({
            rut: data.rut,
            codigo: data.codigo,
            nombre: data.nombre,
            fecha_creacion: data.fecha_creacion,
            descripcion: data.descripcion,
            medidor_id: data.id, // Assuming 'id' is the medidor ID
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        // Fetch the medidor ID before submitting the form
        await loadMedidorId();

        // Submit the medidor data
        await fetch('http://localhost:4000/medidores', {
            method: 'POST',
            body: JSON.stringify(task),
            headers: { 'Content-Type': 'application/json' },
        });

        // Now, you can associate the cliente with the medidor
        await associateClienteMedidor();

        setLoading(false);
        navigate('/');
    };

    const associateClienteMedidor = async () => {
        await fetch('http://localhost:4000/associate', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                cliente_rut: task.rut,
                medidor_id: task.medidor_id,
            }),
        });
    };

    const handleChange = (e) => setTask({ ...task, [e.target.name]: e.target.value });

    const loadTask = async (id) => {
        const res = await fetch(`http://localhost:4000/medidores/${params.id}`);
        const data = await res.json();
        setTask({
            rut: data.rut,
            codigo: data.codigo,
            nombre: data.nombre,
            fecha_creacion: data.fecha_creacion,
            descripcion: data.descripcion,
        });
        setEditing(true);
    };

    useEffect(() => {
        if (params.id) {
            loadTask(params.id);
        }
    }, [params.id]);

    return (
        <Grid container direction='column' alignItems='center' justifyContent='center'>
            <Grid item xs={3}>
                <Card sx={{ mt: 5 }} style={{ backgroundColor: '#1e272e', padding: '1rem' }}>
                    <Typography variant='5' textAlign='center' color='white'>
                        {editing ? "Edit" : "Agregar"}
                    </Typography>
                    <CardContent>
                        <form onSubmit={handleSubmit}>
                            <TextField
                                name='rut'
                                value={task.rut}
                                variant='filled'
                                label='Rut'
                                sx={{ display: 'block', margin: '.5rem 0' }}
                                onChange={handleChange}
                                inputProps={{ style: { color: 'white' } }}
                                InputLabelProps={{ style: { color: 'white' } }} />
                            <TextField
                                name='codigo'
                                value={task.codigo}
                                variant='filled'
                                label='codigo'
                                sx={{ display: 'block', margin: '.5rem 0' }}
                                onChange={handleChange}
                                inputProps={{ style: { color: 'white' } }}
                                InputLabelProps={{ style: { color: 'white' } }} />
                            <TextField
                                name='nombre'
                                value={task.nombre}
                                variant='filled'
                                label='Nombre'
                                sx={{ display: 'block', margin: '.5rem 0' }}
                                onChange={handleChange}
                                inputProps={{ style: { color: 'white' } }}
                                InputLabelProps={{ style: { color: 'white' } }} />
                            <TextField
                                name='fecha_creacion'
                                value={task.fecha_creacion}
                                type='date'
                                variant='filled'
                                label='fecha_creacion'
                                sx={{ display: 'block', margin: '.5rem 0' }}
                                onChange={handleChange}
                                inputProps={{ style: { color: 'white' } }}
                                InputLabelProps={{ style: { color: 'white' } }} />
                            <TextField
                                name='descripcion'
                                value={task.descripcion}
                                variant='filled'
                                label='descripcion'
                                multiline
                                rows={4} sx={{ display: 'block', margin: '.5rem 0' }}
                                onChange={handleChange}
                                inputProps={{ style: { color: 'white' } }}
                                InputLabelProps={{ style: { color: 'white' } }} />
                            <Button variant='contained' color='primary' type='submit' disabled={!task.codigo || !task.nombre || !task.fecha_creacion}>
                                {loading ? <CircularProgress
                                    color='inherit'
                                    size={24}
                                /> : 'Guardar'}
                            </Button>
                        </form>
                    </CardContent>
                </Card>
            </Grid>
        </Grid>
    )
}
